import React from 'react';
import './App.css';
// import $ from 'jquery';
import axios from 'axios';

const getWeatherUrl = (city) => {
  return `https://api.openweathermap.org/data/2.5/weather?q=${city},us&units=imperial&,us&appid=fa85391408d3e1a5d77d51507c0dfd61`;
};

// const getGeocoding = (lat, long) => {
//   return `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${long}&sensor=true&key=AIzaSyCJB7ji6GSFhxbLO0GxtTqrbVa8LrOKkzo`;
// };

class App extends React.Component {
  constructor() {
    super();
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      celcius: true,
      // keywords: '',
    };
  }

  getWeather() {
    axios.get(getWeatherUrl(this.state.city))
    .then(response => {
      console.log(response.data)
      this.setState({
        tempFahrenheit: response.data.main.temp,
        tempCelcius: toCelcius(response.data.main.temp),
        humidity: response.data.main.humidity,
        pressure: response.data.main.pressure,
        tempMin: response.data.main.temp_min,
        tempMax: response.data.main.temp_max,
        seaLevel: response.data.main.sea_level,
        windSpeed: (response.data.wind.speed / .44704).toFixed(2),
        weather: response.data.weather[0].main,
        description: response.data.weather[0].description.capitalize(),
        id: response.data.weather[0].id,
        icon: `http://openweathermap.org/img/w/${response.data.weather[0].icon}.png`,
        sunrise: response.data.sys.sunrise,
        sunset: response.data.sys.sunset,
        latitude: response.data.latitude,
        longitude:  response.data.longitude,
        date: formatDate(new Date()),
        loading: false,
        error: null,
      });
    })
    .catch(err => {
      this.setState({
        loading: false,
        error: err,
      })
    })
  }

  handleClick() {
    this.setState({
      celcius: !this.state.celcius
    });
  }

  componentDidMount() {
    console.log('mounting- mounted')
    // this.getWeather()
    // why doesn't this work? I've read that you should make AJAX reqs 
    // in this lifecycle methods
  }

  render() {
    return (
      <div className="container">

      <Form
        onSubmit={this.handleSubmit}
        onChange={this.handleChange}
        />

        {/* <form onSubmit={this.handleSubmit}>
          <input type="text" onChange={this.handleChange}/>
          <input type="submit" value="Submit"/>
        </form> */}

        <Weather
          description={this.state.description}
          city={this.state.city}
          date={this.state.date}
          temp={this.state.celcius ? this.state.tempFahrenheit : this.state.tempCelcius}
          onClick={this.handleClick}
          country={this.state.country}
        />

        <WeatherBox
          humidity={this.state.humidity}
          pressure={this.state.pressure}
          seaLevel={this.state.seaLevel}
          tempMax={this.state.tempMax}
          tempMin={this.state.tempMin}
          windSpeed={this.state.windSpeed}
          imageSrc={this.state.icon
              ? `https://res.cloudinary.com/lucedesign/image/upload/v1509486590/svg%20weather%20icons/${idIconLookup[this.state.id]}`
              : `https://res.cloudinary.com/lucedesign/image/upload/v1509486574/svg%20weather%20icons/weather.svg`
          }
          country={this.state.country}
        />
      </div>
    )
  }
}



class Form extends React.Component{
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);

  }

  handleSubmit(e) {
    console.log(this.props.city)
    this.setState({
      city: this.props.inputValue,
    })
    console.log(this.props.city)

    this.getWeather()
    e.preventDefault()
    console.log('City: ' + this.props.city + ' was submitted for get request.')
    // this.setState({ city: this.state.keywords })
  }

  handleChange(e) {
    this.setState({
      inputValue: e.target.value.split(' ').join('+'),
    })
    console.log(this.props.inputValue)
  }

  render() {
    return (
      <div>
        <form onSubmit={this.props.handleSubmit}>
          Enter city name for weather:
          <input type="text" onChange={this.props.handleChange}/>
          <input type="submit" value="Submit"/>
        </form>
      </div>
    )
  }
}


// Contains the weather, description, city, date, and temp
const Weather = props => (
  <div>
    <p>{props.description ? `${props.description} in ` : ""}
      <span>
        {props.city ? `${props.city.capitalize()}.` : ""}
      </span>
    </p>

    <p>{props.date ? `${props.date}` : ""}</p>

    <p onClick={props.onClick}>
      {props.temp}°F
    </p>

  </div>
);


// The rounded weather box that contains the icon and additional weather data
const WeatherBox = props => (
  <div>
    <div className="humidity container">
      <p>{props.pressure ? `Pressure: ${props.pressure} hPa` : ""}</p>
      <p>{props.humidity ? `Humidity: ${props.humidity}%` : ""}</p>
      <p>{props.seaLevel ? `Sea Level: ${props.seaLevel}` : ""}</p>
    </div>
    <img src={props.imageSrc} alt='filler'/>
    <div className="tempMax container">
      <p>{props.tempMax ? `Max Temp: ${props.tempMax}°` : ""}</p>
      <p>{props.tempMin ? `Min Temp: ${props.tempMin}°` : ""}</p>
      <p>{props.windSpeed ? `Wind Speed: ${props.windSpeed} mph` : ""}</p>
    </div>
  </div>
);

// Add a function to the String type to capitalize all letters in a sentence
String.prototype.capitalize = function() {
  return this.replace(/(?:^|\s)\S/g, function(a) {
    return a.toUpperCase();
  });
};

// Helper functions -------------------------------------------------
let toCelcius = (temp) => ((temp - 32) * 5 / 9).toFixed(1);


// Check if it's between 6am and 6pm and return true or false
let isDayTime = () => {
  let date = new Date();
  let hour = date.getHours();
  if (hour < 18 && hour > 6) {
    return true;
  } else {
    return false;
  }
};


// Get a pretty date
let formatDate = (date) => {
  let monthNames = [
    "January", "February", "March",
    "April", "May", "June", "July",
    "August", "September", "October",
    "November", "December"
  ];

  let day = date.getDate();
  let monthIndex = date.getMonth();
  let year = date.getFullYear();

  return `${monthNames[monthIndex]} ${day}, ${year}`;
}



// Icon Lookup Table ----------------------------------------------------------------------------
let idIconLookup = {
  200: "thunder.svg",
  201: "thunder.svg",
  202: "thunder.svg",
  210: "thunder.svg",
  211: "thunder.svg",
  212: "thunder.svg",
  221: "thunder.svg",
  230: "thunder.svg",
  231: "thunder.svg",
  232: "thunder.svg",
  300: "rainy-4.svg",
  301: "rainy-4.svg",
  302: "rainy-4.svg",
  310: "rainy-5.svg",
  311: "rainy-5.svg",
  312: "rainy-5.svg",
  313: "rainy-6.svg",
  314: "rainy-6.svg",
  321: "rainy-6.svg",
  500: "rainy-7.svg",
  501: "rainy-7.svg",
  502: "rainy-7.svg",
  503: "rainy-7.svg",
  504: "rainy-7.svg",
  511: "rainy-7.svg",
  520: "rainy-7.svg",
  521: "rainy-7.svg",
  522: "rainy-7.svg",
  531: "rainy-7.svg",
  600: "snowy-4.svg",
  601: "snowy-4.svg",
  611: "snowy-4.svg",
  612: "snowy-5.svg",
  615: "snowy-5.svg",
  616: "snowy-5.svg",
  620: "snowy-6.svg",
  621: "snow-6.svg",
  622: "snowy-6.svg",
  701: "cloudy.svg",
  711: "cloudy.svg",
  721: "cloudy.svg",
  731: "cloudy.svg",
  741: "cloudy.svg",
  751: "cloudy.svg",
  761: "cloudy.svg",
  762: "cloudy.svg",
  771: "cloudy.svg",
  781: "cloudy.svg",
  800: isDayTime() ? "day.svg" : "night.svg",
  801: isDayTime() ? "cloudy-day-3.svg" : "cloudy-night-3.svg",
  802: isDayTime() ? "cloudy-day-3.svg" : "cloudy-night-3.svg",
  803: isDayTime() ? "cloudy-day-3.svg" : "cloudy-night-3.svg",
  804: isDayTime() ? "cloudy-day-3.svg" : "cloudy-night-3.svg"
};

export default App;
