import React from 'react';
import './App.css';
import axios from 'axios';

const getWeatherUrl = (city) => {
  return `https://api.openweathermap.org/data/2.5/weather?q=${city},us&units=imperial&,us&appid=fa85391408d3e1a5d77d51507c0dfd61`;
};

// const getGeocoding = (lat, long) => {
//   return `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${long}&sensor=true&key=AIzaSyCJB7ji6GSFhxbLO0GxtTqrbVa8LrOKkzo`;
// };
// will introduce the google maps api for in app map views eventually

class App extends React.Component {
  constructor() {
    super();
    // this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {
      celcius: false,
    };
  }

  getWeather() {
    axios.get(getWeatherUrl(this.state.city))
    .then(response => {
      console.log(response.data)
      this.setState({
        tempFahrenheit: response.data.main.temp,
        tempCelcius: toCelcius(response.data.main.temp),
        humidity: response.data.main.humidity,
        pressure: response.data.main.pressure,
        tempMin: response.data.main.temp_min,
        tempMax: response.data.main.temp_max,
        windSpeed: (response.data.wind.speed / .44704).toFixed(2),
        weather: response.data.weather[0].main,
        country: response.data.sys.country,
        description: response.data.weather[0].description.capitalize(),
        id: response.data.weather[0].id,
        icon: `http://openweathermap.org/img/w/${response.data.weather[0].icon}.png`,
        sunrise: formatTime(response.data.sys.sunrise),
        sunset: formatTime(response.data.sys.sunset),
        latitude: response.data.latitude,
        longitude:  response.data.longitude,
        date: formatDate(new Date()),
        loading: false,
        error: null,
      });
    })
    .catch(err => {
      this.setState({
        loading: false,
        error: err,
      })
    })
  }

  handleSubmit(e) {
    this.setState({
      city: this.state.inputValue,
    })
    console.log(this.state.city)
    this.getWeather()
    e.preventDefault()
    console.log('City: ' + this.state.city + ' was submitted for get request.')
  }

  handleChange(e) {
    this.setState({
      inputValue: e.target.value.split(' ').join('+'),
    })
    console.log(this.state.inputValue)
  }


  componentDidMount() {
    console.log('mounting- mounted')
    // this.getWeather()
    // why doesn't this work? I've read that you should make AJAX reqs 
    // in this lifecycle methods
  }

  render() {
    return (
      <div className="container">
        {console.log('Note from jamesroyston: You have to submit/hit enter twice in order to get the correct weather for the entered city. Still debugging that bit.')}
        <form className="form" onSubmit={this.handleSubmit}>
          <input className="inputField" type="text" onChange={this.handleChange} placeholder="Enter City"/>
          <input className="submitButton" type="submit" value="Submit"/>
        </form>
        <Weather
          imageSrc={this.state.icon
            ? `https://res.cloudinary.com/lucedesign/image/upload/v1509486590/svg%20weather%20icons/${idIconLookup[this.state.id]}`
            : `https://res.cloudinary.com/lucedesign/image/upload/v1509486574/svg%20weather%20icons/weather.svg`
          }
          description={this.state.description}
          city={this.state.city}
          date={this.state.date}
          temp={this.state.celcius ? `Current: ${this.state.tempCelcius}` : `Current: ${this.state.tempFahrenheit}`}
          onClick={this.handleClick}
          country={this.state.country}
        />

        <WeatherBox
          sunMovement={isDayTime() ? `Sunset: ${this.state.sunset}` :
           `Sunrise: ${this.state.sunrise}`}
          humidity={this.state.humidity}
          pressure={this.state.pressure}
          tempMax={this.state.tempMax}
          tempMin={this.state.tempMin}
          windSpeed={this.state.windSpeed}

        />
      </div>
    )
  }
}


const Weather = props => (
  <div>
    <img className="weatherIcon" src={props.imageSrc} alt='filler'/>
    <p className="description">{props.description ? `${props.description} in ` : ""}
      <span>
        {props.city ? `${props.city.capitalize()}, ${props.country}` : ""}
      </span>
    </p>
    <p>{props.date ? `${props.date}` : ""}</p>
    <p onClick={props.onClick}>
      {props.temp ? `${props.temp}˚F` : ""}
    </p>
  </div>
);


const WeatherBox = props => (
  <div>

    <p>
    {props.tempMin && props.tempMax ?
    `Lo/Hi : ${props.tempMin}°F / ${props.tempMax}˚F` : ""}
    </p>
    <p>{props.sunMovement} <span>Current: {formatTime(currentTime)}</span></p>
    <p>{props.windSpeed ? `Wind Speed: ${props.windSpeed} mph` : ""}</p>
    <p>{props.humidity ? `Humidity: ${props.humidity}%` : ""}</p>
    <p>{props.pressure ? `Pressure: ${props.pressure} hPa` : ""}</p>
  </div>
);

// Add a function to the String type to capitalize all letters in a sentence
String.prototype.capitalize = function() {
  return this.replace(/(?:^|\s)\S/g, function(a) {
    return a.toUpperCase();
  });
};

// Helper functions -------------------------------------------------
let toCelcius = (temp) => ((temp - 32) * 5 / 9).toFixed(1);


// Check if it's between 6am and 6pm and return true or false
let isDayTime = () => {
  let date = new Date();
  let hour = date.getHours();
  if (hour < 18 && hour > 6) {
    return true;
  } else {
    return false;
  }
};

let formatTime = (time) => {
  let date = new Date(time*1000);
// Hours part from the timestamp
  let hours = date.getHours();
  hours = (hours > 12) ? hours - 12 : false;
// Minutes part from the timestamp
  let minutes = "0" + date.getMinutes();
// Will display time in HH:MM format
  let formattedTime = hours + ':' + minutes.substr(-2)
  return formattedTime + (isDayTime() ? 'PM' : 'AM')
};

let currentTime = () => {
  let date = new Date();
  let hours = date.getHours();
  let minutes = "0" + date.getMinutes();
  let formattedTime = hours + ':' + minutes.substr(-2)
  return formattedTime + (isDayTime() ? 'PM' : 'AM');
}



// Get a pretty date
let formatDate = (date) => {
  let monthNames = [
    "January", "February", "March",
    "April", "May", "June", "July",
    "August", "September", "October",
    "November", "December"
  ];

  let day = date.getDate();
  let monthIndex = date.getMonth();
  let year = date.getFullYear();

  return `${monthNames[monthIndex]} ${day}, ${year}`;
}



// Icon Lookup Table ----------------------------------------------------------------------------
let idIconLookup = {
  200: "thunder.svg",
  201: "thunder.svg",
  202: "thunder.svg",
  210: "thunder.svg",
  211: "thunder.svg",
  212: "thunder.svg",
  221: "thunder.svg",
  230: "thunder.svg",
  231: "thunder.svg",
  232: "thunder.svg",
  300: "rainy-4.svg",
  301: "rainy-4.svg",
  302: "rainy-4.svg",
  310: "rainy-5.svg",
  311: "rainy-5.svg",
  312: "rainy-5.svg",
  313: "rainy-6.svg",
  314: "rainy-6.svg",
  321: "rainy-6.svg",
  500: "rainy-7.svg",
  501: "rainy-7.svg",
  502: "rainy-7.svg",
  503: "rainy-7.svg",
  504: "rainy-7.svg",
  511: "rainy-7.svg",
  520: "rainy-7.svg",
  521: "rainy-7.svg",
  522: "rainy-7.svg",
  531: "rainy-7.svg",
  600: "snowy-4.svg",
  601: "snowy-4.svg",
  611: "snowy-4.svg",
  612: "snowy-5.svg",
  615: "snowy-5.svg",
  616: "snowy-5.svg",
  620: "snowy-6.svg",
  621: "snow-6.svg",
  622: "snowy-6.svg",
  701: "cloudy.svg",
  711: "cloudy.svg",
  721: "cloudy.svg",
  731: "cloudy.svg",
  741: "cloudy.svg",
  751: "cloudy.svg",
  761: "cloudy.svg",
  762: "cloudy.svg",
  771: "cloudy.svg",
  781: "cloudy.svg",
  800: isDayTime() ? "day.svg" : "night.svg",
  801: isDayTime() ? "cloudy-day-3.svg" : "cloudy-night-3.svg",
  802: isDayTime() ? "cloudy-day-3.svg" : "cloudy-night-3.svg",
  803: isDayTime() ? "cloudy-day-3.svg" : "cloudy-night-3.svg",
  804: isDayTime() ? "cloudy-day-3.svg" : "cloudy-night-3.svg"
};

export default App;
